package com.rf1m.image2css.service

import com.rf1m.image2css.config.CommonContextConfiguration
import com.rf1m.image2css.domain.CssClass
import com.rf1m.image2css.exception.Image2CssValidationException
import org.springframework.context.ApplicationContext
import org.springframework.context.annotation.AnnotationConfigApplicationContext
import spock.lang.Specification


class DefaultImageConversionServiceSpec extends Specification {

    DefaultImageConversionService defaultImageConversionService

    def setup() {
        ApplicationContext ctx = new AnnotationConfigApplicationContext(CommonContextConfiguration.class)
        defaultImageConversionService = ctx.getBean(DefaultImageConversionService.class)
    }

    def "should throw validation exception if file parameter is null"() {
        setup:
            File file = null
        when:
            defaultImageConversionService.convert(file)
        then:
            thrown(Image2CssValidationException.class)
    }

    def "should throw validation exception if file parameter references file that does not exist"() {
        setup:
            File file = new File("/tmp/${System.currentTimeMillis()}")
        when:
            defaultImageConversionService.convert(file)
        then:
            thrown(Image2CssValidationException.class)
    }

    def "should throw validation exception if file parameter references file that is a directory"() {
        setup:
            File file = new File(System.getProperty("user.dir"))
        when:
            defaultImageConversionService.convert(file)
        then:
            thrown(Image2CssValidationException.class)
    }

    def "should throw validation exception if file parameter references an unsupported file extension"() {
        setup:
            URL url = this.class.getClassLoader().getResource("test.tiff")
            File file = new File(url.toURI())
        when:
            defaultImageConversionService.convert(file)
        then:
            thrown(Image2CssValidationException.class)
    }

    def "should return a CssClass representation of a jpg image file"() {
        setup:
            URL url = this.class.getClassLoader().getResource("test.jpg")
            File file = new File(url.toURI())
        when:
            CssClass result = defaultImageConversionService.convert(file)
        then:
            result.name == "test_jpg"
            result.body == ".test_jpg{\n\tbackground:url(data:image/jpg;base64,) top left no-repeat;);\n\twidth:12px;\n\theight:14px;\n}"
    }

    def "should get image at url, convert, and return CssClass representation "() {
        setup:
            URL url = new URL("https://cloud.githubusercontent.com/assets/704470/5594435/08120610-9211-11e4-8d47-ac98324243a7.jpg")
        when:
            CssClass result = defaultImageConversionService.convert(url)
        then:
            result.name == "_assets_704470_5594435_08120610_9211_11e4_8d47_ac98324243a7_jpg"
            result.body == "._assets_704470_5594435_08120610_9211_11e4_8d47_ac98324243a7_jpg{\n\tbackground:url(data:image/jpeg;base64,) top left no-repeat;);\n\twidth:12px;\n\theight:14px;\n}"
    }

    def "should get image at url based on string, convert, and return CssClass representation "() {
        setup:
            String urlValue = "https://cloud.githubusercontent.com/assets/704470/5594435/08120610-9211-11e4-8d47-ac98324243a7.jpg"
        when:
            CssClass result = defaultImageConversionService.convert(urlValue)
        then:
            result.name == "_assets_704470_5594435_08120610_9211_11e4_8d47_ac98324243a7_jpg"
            result.body == "._assets_704470_5594435_08120610_9211_11e4_8d47_ac98324243a7_jpg{\n\tbackground:url(data:image/jpeg;base64,) top left no-repeat;);\n\twidth:12px;\n\theight:14px;\n}"
    }

}
